mapper.select:
select X.*
, nvl((select 'Y' from dual where exists (select 1 from airbnb_wishlists W, airbnb_wishlist_accommodations WA where W.wishlist_no = WA.wishlist_no and W.user_no = #{user.no} and WA.acc_no = X.acc_no)), 'N') SAVED_WISHLIST
, 'N' as WISHLIST_SAVED
from ( select A.* from airbnb_accommodations A
, airbnb_tags B, airbnb_acc_tags C
A.acc_no = C.acc_no and B.tag_id = C.tag_id and B.tag_id = #{id}
and A.acc_price >= #{minPrice} and A.acc_price <= #{maxPrice}
and A.acc_address like '%'||#{keyword}||'%'
and A.acc_check_in <= #{startDate} and A.acc_check_out >= #{endDate}
and A.acc_guest >= #{adult} + #{child} + #{infant}
and A.acc_pet = #{pet}
and A.acc_no in (select acc_no from airbnb_acc_types where type_no in
#{type}
)
and A.acc_no in (select acc_no from airbnb_acc_types where type_no in
#{type}
)
and A.acc_no in (select acc_no from airbnb_acc_conveniences where convenience_no in
#{convenience}
)
and A.acc_no in (select acc_no from airbnb_acc_rooms)
and A.acc_no in (select acc_no from airbnb_acc_rooms where room_bedroom >= 8)
and A.acc_no in (select acc_no from airbnb_acc_rooms where room_bedroom = #{bedrooms})
and A.acc_no in (select acc_no from airbnb_acc_rooms)
and A.acc_no in (select acc_no from airbnb_acc_rooms where room_bed >= 8)
and A.acc_no in (select acc_no from airbnb_acc_rooms where room_bed = #{beds})
and A.acc_no in (select acc_no from airbnb_acc_rooms)
and A.acc_no in (select acc_no from airbnb_acc_rooms where room_bathroom >= 8)
and A.acc_no in (select acc_no from airbnb_acc_rooms where room_bathroom = #{bathrooms})
order by A.acc_review_count desc, A.acc_review_score desc) X where rownum <= 20

select Max(acc_no)
from airbnb_accommodations
where user_no = #{no}

mapper.insert:
INSERT INTO airbnb_acc_types (acc_no, type_no)
SELECT #{accNo}
     , #{type.no}
  FROM dual
 WHERE NOT EXISTS (SELECT *
                     FROM airbnb_acc_types
                    WHERE type_no = #{type.no}
                    and acc_no = #{accNo})
