mapper.select:
select acc_no as accNo, room_bedroom as bedroom, room_bed as bed, room_bathroom as bathroom
from airbnb_acc_rooms
where acc_no = #{value}

mapper.insert:
INSERT INTO airbnb_acc_types (acc_no, type_no)
SELECT #{accNo}
     , #{type.no}
  FROM dual
 WHERE NOT EXISTS (SELECT *
                     FROM airbnb_acc_types
                    WHERE type_no = #{type.no}
                    and acc_no = #{accNo})

mapper.update:
update airbnb_acc_types
set 
	type_no = #{type.no}
where acc_no = #{accNo}
and type_no in (1, 2, 3, 4, 5, 6)

update airbnb_acc_types
set 
	type_no = #{type.no}
where acc_no = #{accNo}
and type_no between 10 and 87

update airbnb_acc_types
set 
	type_no = #{type.no}
where acc_no = #{accNo}
and type_no in (100, 101, 102)

update airbnb_accommodations
set 
	acc_guest = #{guest}
where acc_no = #{accNo}

update airbnb_accommodations
set 
	acc_address = #{address},
	acc_latitude = #{latitude},
	acc_longitude = #{longitude}
where acc_no = #{accNo}

update airbnb_accommodations
set 
	image_cover = #{imageCover}
where acc_no = #{accNo}

update airbnb_accommodations
set 
	acc_name = #{name}
where acc_no = #{accNo}

update airbnb_accommodations
set 
	acc_description = #{description}
where acc_no = #{accNo}

update airbnb_accommodations
set 
	acc_price = #{price}
where acc_no = #{accNo}

update airbnb_accommodations
set 
	acc_status = #{status}
where acc_no = #{accNo}
